//import java.rmi.*;import java.rmi.registry.LocateRegistry;import java.rmi.registry.Registry;import java.rmi.RemoteException;import java.util.Vector;import java.awt.Rectangle;import java.awt.Color;public class ShapeListClient{   public static void main(String args[]){   	String option = "Read";	String shapeType = "Rectangle";                      System.setProperty("java.security.policy", "policy.txt");        /*if(System.getSecurityManager() == null){        	System.setSecurityManager(new RMISecurityManager());        }         else            System.out.println("Already has a security manager, so cant set RMI SM");         ShapeList aShapeList = null; */                  try{            //ShapeList aShapeList  = (ShapeList) Naming.lookup("//localhost/ShapeList");                         // Obtener el registro RMI            Registry registry = LocateRegistry.getRegistry("localhost", 1999);           // Registry registry = LocateRegistry.getRegistry("localhost", 1199);            // Buscar el objeto remoto por su nombre en el registro RMI            ShapeList aShapeList = (ShapeList) registry.lookup("ShapeList");                        System.out.println("Found server");                                            //Crear algunos objetos            GraphicalObject g = new GraphicalObject(shapeType,                        new Rectangle(50,50,300,400),Color.blue,                  			Color.blue, false);             System.out.println("Created graphical object");                         //Llamado remoto para agregar              aShapeList.newShape(g);                             g = new GraphicalObject(shapeType,                        new Rectangle(50,50,100,100),Color.red,                  			Color.red, true);              System.out.println("Created graphical object");                          //Llamado remoto para agregar                          aShapeList.newShape(g);                        System.out.println("Stored X shapes");                                    Vector sList = aShapeList.allShapes();            System.out.println("Got vector");            int lv = aShapeList.getVersion();             System.out.println("List version: "+ lv);                        for(int i=0; i<sList.size(); i++){        	g = ((Shape)sList.elementAt(i)).getAllState();        	g.print();                int sv = ((Shape) sList.elementAt(i)).getVersion();                System.out.println("Shape version: "+ sv );        	}     	  }catch(RemoteException e) {System.out.println("allShapes: " + e.getMessage());	}catch(Exception e) {System.out.println("Lookup: " + e.getMessage());}    }}	             