//import java.rmi.*;import java.rmi.registry.LocateRegistry;import java.rmi.registry.Registry;import java.rmi.RemoteException;public class ShapeListServer {	public static void main(String args[]){        System.setProperty("java.security.policy", "policy.txt");	/* if (System.getSecurityManager() == null) {	   System.setSecurityManager(new RMISecurityManager());} */         System.out.println("Main OK");        try{            //Crea objeto remoto            ShapeList aShapelist = new ShapeListImpl();            System.out.println("After create");                        //registra el objeto            //Naming.rebind("ShapeList", aShapelist);   //metodo anterior                        // Iniciar el registro RMI en el puerto 1999            Registry registry = LocateRegistry.createRegistry(1999);            // Vincular el objeto remoto en el registro RMI            registry.rebind("ShapeList", aShapelist);                                   //Crea otro objeto remoto, para mostra lista de objetos            ShapeList Shapelist2 = new ShapeListImpl();            registry.rebind("UnaSegundaShapeList", Shapelist2);            registry.unbind("UnaSegundaShapeList"); //libera                        String [] lista = registry.list();                        //Lista de objetos registrados            for (String lista1 : lista) {               System.out.println(lista1);            }                                      System.out.println("ShapeList server ready!");                    }catch(Exception e) {            System.out.println("ShapeList server main " + e.getMessage());        }    }}